<script>

    // Initialize map and markers
    $(document).ready(function () {
        let properties = JSON.parse($('#properties_data').val());
        var el = $("#view_map");
        var centerLatLng = new google.maps.LatLng(properties.lat, properties.lng);

        var options = {
            mapTypeId: google.maps.MapTypeId.ROADMAP,
            zoom: 15,
            center: centerLatLng,
            styles: getMapStyle()
        };
        map = new google.maps.Map(el.get(0), options);

        let total_info_windows = [];

        $(properties.data).each(function (i, property) {
            const infowindow = new google.maps.InfoWindow({
                content: makeHtmlContent(property)
            });
            total_info_windows.push(infowindow);
            const marker = new google.maps.Marker({
                position: new google.maps.LatLng(property.lat, property.lng),
                map,
                title: property.no_of_bed + ' Beds ' + property.flat_type,
                animation: google.maps.Animation.DROP,
                icon: '/images/pin.png',
                id: 'pin_' + property.id
            });

            marker.addListener("click", () => {
                for (i = 0; i < total_info_windows.length; i++) {
                    total_info_windows[i].close();
                }
                infowindow.open(map, marker);
                // google.maps.event.trigger(markers[property.id], 'click');
            });
        });

        google.maps.event.addListener(map, "click", function (event) {
            for (i = 0; i < total_info_windows.length; i++) {
                total_info_windows[i].close();
            }
        });
    });

