    // Initialize auto complete search places
    function initAutocomplete() {
        let input = document.getElementById('location_search');
        let options = {
            // type: ['bus_station''],
            // types: ['(regions)'],
            // types: ['address'],
            // types: ['establishment'],
            // types: ['geocode'],
            // types: ['(cities)'],
            componentRestrictions: {country: 'uk'},
        };

        let autocomplete = new google.maps.places.Autocomplete(input, options);
        google.maps.event.addListener(autocomplete, 'place_changed', function () {
            let selectedPlace = autocomplete.getPlace();
            if (selectedPlace.length == 0) {
                return;
            }
            let location = $('#location_search').val();
            let lat = selectedPlace.geometry.location.lat();
            let lng = selectedPlace.geometry.location.lng();
            window.location = '/search/' + location + '/' + lat + '/' + lng + '/all/no_minimum/0/miles/2/50/15000';

        });
    }

