    function makeHtmlContent(property) {
        let sliderId = 'map_pin_card_slider_' + property.id;
        $(document).ready(function () {
            //Event for pushed the video
            $('#' + sliderId).carousel({
                pause: true,
                interval: false
            });
        });

        var imageItems = '';
        if (property.images.length) {
            $(property.images).each(function (i, img) {
                var activeClass = i == 0 ? 'active' : '';
                imageItems += `<div class="carousel-item ` + activeClass + `">
                                        <img src="` + img.image + `" alt="">
                                    </div>`;
            })
        }
        return `
                    <div class="map-pin-card">
                        <a href="/property/show/` + property.id + `" target="_blank">
                              <div id="` + sliderId + `" class="carousel slide" data-ride="carousel">
                                  <div class="carousel-inner">
                                        ` + imageItems + `
                                  </div>
                                  <a class="carousel-control-prev" href="#` + sliderId + `" role="button" data-slide="prev">
                                      <span class="carousel-control-prev-icon" aria-hidden="true"><i class="fa fa-angle-left" aria-hidden="true"></i></span>
                                      <span class="sr-only">Previous</span>
                                  </a>
                                  <a class="carousel-control-next" href="#` + sliderId + `" role="button" data-slide="next">
                                      <span class="carousel-control-next-icon" aria-hidden="true"><i class="fa fa-angle-right" aria-hidden="true"></i></span>
                                      <span class="sr-only">Next</span>
                                  </a>
                              </div>

                              <div class="map-pin-card-details">
                                  <span class="like-icon"></span>
                                  <div class="map-pin-card-property-type">
                                      <p>` + property.no_of_bed + ' Beds ' + property.flat_type + `</p>
                                  </div>
                                  <div class="map-pin-card-property-location">
                                      <p> ` + property.flat_house + ', ' + property.town + `</p>
                                  </div>
                                  <div class="pricing">
                                      <h4>£` + property.monthly_amount + ` <span>/ month</span></h4>
                                  </div>
                              </div>
                         </a>
                    </div>
            `;
    }

