            }
        });

        window.location = '/search/London E10, UK/51.5649619/-0.0146911/all/no_minimum/0/miles/2/50/15000/' + JSON.stringify(features) + '/' + JSON.stringify(preferred) + '/' + JSON.stringify(furnishing);
    }

    function filterApplyMobile() {
        let distance_type = $('#distance_type_mob').val();
        let radius = $('#radius-mob').val();
        let min = parseInt($('#slider-range-value1-mob').text().replace('£', '').replace(',', ''));
        let max = parseInt($('#slider-range-value2-mob').text().replace('£', '').replace(',', ''));

        var features = [];
        var preferred = [];
        var furnishing = [];
        $('#all_mob_features input[type=checkbox]').each(function (i, el) {
            if ($(el).is(':checked')) {
                features.push($(el).attr('data-id'))
            }
        });

        $('#all_mob_preferred input[type=checkbox]').each(function (i, el) {
            if ($(el).is(':checked')) {
                preferred.push($(el).attr('data-id'))
            }
        });

        $('#all_mob_furnishing input[type=checkbox]').each(function (i, el) {
            if ($(el).is(':checked')) {
                furnishing.push($(el).attr('data-value'))
            }
        });
        window.location = '/search/London E10, UK/51.5649619/-0.0146911/all/no_minimum/0/' + distance_type + '/' + radius + '/' + min + '/' + max + '/' + JSON.stringify(features) + '/' + JSON.stringify(preferred) + '/' + JSON.stringify(furnishing);

    }

    // onload pre select features, preferred etc filters setting
    selected_features = JSON.parse($('#selected_features').val());
    selected_preferred = JSON.parse($('#selected_preferred').val());
    selected_furnishing = JSON.parse($('#selected_furnishing').val());
    if (selected_features) {
        $(selected_features).each(function (i, id) {
            $('.f_' + id).prop('checked', true);
        });
    }
    if (selected_preferred) {
        $(selected_preferred).each(function (i, id) {
            $('.p_' + id).prop('checked', true);
        });
    }
    if (selected_furnishing) {
        $(selected_furnishing).each(function (i, id) {
            $('.' + id.replaceAll(' ', '-')).prop('checked', true);
        });
    }
</script>
